import { createContext, useState } from 'react'
import jsTPS from '../common/jsTPS'
import api from '../api'
import MoveSong_Transaction from '../common/MoveSong_Transaction';
export const GlobalStoreContext = createContext({});
/*
    This is our global data store. Note that it uses the Flux design pattern,
    which makes use of things like actions and reducers. 

    @author McKilla Gorilla
*/

// THESE ARE ALL THE TYPES OF UPDATES TO OUR GLOBAL
// DATA STORE STATE THAT CAN BE PROCESSED
export const GlobalStoreActionType = {
    CHANGE_LIST_NAME: "CHANGE_LIST_NAME",
    CLOSE_CURRENT_LIST: "CLOSE_CURRENT_LIST",
    CREATE_NEW_LIST: "CREATE_NEW_LIST",
    UPDATE_CURRENT_LIST:"UPDATE_CURRENT_LIST",
    LOAD_ID_NAME_PAIRS: "LOAD_ID_NAME_PAIRS",
    SET_CURRENT_LIST: "SET_CURRENT_LIST",
    SET_LIST_NAME_EDIT_ACTIVE: "SET_LIST_NAME_EDIT_ACTIVE",
    MARK_DELETE_LIST: "MARK_DELETE_LIST",
    MARK_DELETE_SONG: "MARK_DELETE_SONG",
    // MARK_EDIT_SONG:"MARK_EDIT_SONG",
}

// WE'LL NEED THIS TO PROCESS TRANSACTIONS
const tps = new jsTPS();

// WITH THIS WE'RE MAKING OUR GLOBAL DATA STORE
// AVAILABLE TO THE REST OF THE APPLICATION
export const useGlobalStore = () => {
    // THESE ARE ALL THE THINGS OUR DATA STORE WILL MANAGE
    const [store, setStore] = useState({
        idNamePairs: [],
        currentList: null,
        newListCounter: 0,
        listNameActive: false,
        markDeleteList: null,
        markDeleteSong: null,
    });

    // HERE'S THE DATA STORE'S REDUCER, IT MUST
    // HANDLE EVERY TYPE OF STATE CHANGE
    const storeReducer = (action) => {
        const { type, payload } = action;
        switch (type) {
            // LIST UPDATE OF ITS NAME
            case GlobalStoreActionType.CHANGE_LIST_NAME: {
                return setStore({
                    idNamePairs: payload.idNamePairs,
                    currentList: payload.playlist,
                    newListCounter: store.newListCounter,
                    listNameActive: false,
                    markDeleteList: store.markDeleteList,
                    markDeleteSong: null
                });
            }
            // STOP EDITING THE CURRENT LIST
            case GlobalStoreActionType.CLOSE_CURRENT_LIST: {
                return setStore({
                    idNamePairs: store.idNamePairs,
                    currentList: null,
                    newListCounter: store.newListCounter,
                    listNameActive: false,
                    markDeleteList: store.markDeleteList,
                    markDeleteSong: null
                })
            }
            // CREATE A NEW LIST
            case GlobalStoreActionType.CREATE_NEW_LIST: {
                return setStore({
                    idNamePairs: store.idNamePairs,
                    currentList: payload,
                    newListCounter: store.newListCounter + 1,
                    listNameActive: false,
                    markDeleteList: store.markDeleteList,
                    markDeleteSong: store.markDeleteSong
                })
            }
            // GET ALL THE LISTS SO WE CAN PRESENT THEM
            case GlobalStoreActionType.LOAD_ID_NAME_PAIRS: {
                return setStore({
                    idNamePairs: payload,
                    currentList: null,
                    newListCounter: store.newListCounter,
                    listNameActive: false,
                    markDeleteList: store.markDeleteList,
                    markDeleteSong: store.markDeleteSong
                });
            }
            // PREPARE TO DELETE A LIST
            case GlobalStoreActionType.MARK_LIST_FOR_DELETION: {
                return setStore({
                    idNamePairs: store.idNamePairs,
                    currentList: null,
                    newListCounter: store.newListCounter,
                    listNameActive: false,
                    markDeleteList: payload,
                    markDeleteSong: store.markDeleteSong
                });
            }
            // UPDATE A LIST
            case GlobalStoreActionType.SET_CURRENT_LIST: {
                return setStore({
                    idNamePairs: store.idNamePairs,
                    currentList: payload,
                    newListCounter: store.newListCounter,
                    listNameActive: false,
                    markDeleteList: store.markDeleteList,
                    markDeleteSong: store.markDeleteSong
                });
            }
            // START EDITING A LIST NAME
            case GlobalStoreActionType.SET_LIST_NAME_EDIT_ACTIVE: {
                return setStore({
                    idNamePairs: store.idNamePairs,
                    currentList: payload,
                    newListCounter: store.newListCounter,
                    listNameActive: true,
                    markDeleteList: store.markDeleteList,
                    markDeleteSong: store.markDeleteSong
                });
            }
            case GlobalStoreActionType.UPDATE_CURRENT_LIST:{
                return setStore({
                    idNamePairs: store.idNamePairs,
                    currentList: payload,
                    newListCounter: store.newListCounter,
                    listNameActive: store.listNameActive,
                    markDeleteList: store.markDeleteList,
                    markDeleteSong: store.markDeleteSong
                });
            }
            case GlobalStoreActionType.MARK_DELETE_SONG:{
                return setStore({
                    idNamePairs: store.idNamePairs,
                    currentList: store.currentList,
                    newListCounter: store.newListCounter,
                    listNameActive: false,
                    markDeleteList: store.markDeleteList,
                    markDeleteSong: payload,
                });
            }

            default:
                return store;
        }
    }

    store.deletelist =  function(){


        let id = store.markDeleteList._id
        let name = store.markDeleteList.name
        console.log("id: "+ id)
        async function deletelist(id){
            let response = await api.deletePlaylistById(id);
            let name = store.markDeleteList.name
            console.log("Name: "+name)
            storeReducer({
                type: GlobalStoreActionType.CREATE_NEW_LIST,
                payload: name
            });     
           // store.setCurrentList(newListId);   
           store.loadIdNamePairs() 
        }
        deletelist(id);
    }

    store.showDeleteListModal = function (id){
        let markedList = store.idNamePairs.filter(list => list._id === id)[0];
        let modal = document.getElementById("delete-list-modal");

        storeReducer({
            type: GlobalStoreActionType.MARK_LIST_FOR_DELETION,
            payload: markedList
        });
        modal.classList.add("is-visible");
    }

    store.hideDeleteListModal = function (){
        let modal = document.getElementById("delete-list-modal");
        modal.classList.remove("is-visible");
    }

    store.showDeleteSongModal = function(index){
        let modal = document.getElementById("delete-song-modal");
        // console.log("test")
        // console.log("index: "+ index)
        // console.log("song delete: "+ this.currentList.songs[index].title)
        storeReducer({
            type: GlobalStoreActionType.MARK_DELETE_SONG,
            payload: index
        });     

        //console.log("show delete song modal: "+ store.markDeleteSong)

        modal.classList.add("is-visible");
    }

    store.hideDeleteSongModal = function(){
        let modal = document.getElementById("delete-song-modal");
        modal.classList.remove("is-visible");
    }




    store.createNewList = function (){
        let newlist = {
            name:"Untitled",
            songs:[]
        }
        async function asyncCreateNewList(){
            let response = await api.createPlaylist(newlist);
            let playlist=response.data.playlist;
            let newListId=playlist._id;

            storeReducer({
                type: GlobalStoreActionType.CREATE_NEW_LIST,
                payload: playlist
            });     
            store.setCurrentList(newListId);                                           
        }
        asyncCreateNewList();

    }

    store.addNewSong = async function(){
        let newSong = {
            artist: "Unknown", title: "Untitled", youTubeId: "dQw4w9WgXcQ"
        }
        this.currentList.songs.push(newSong)
            await api.updatePlaylistById(this.currentList._id,this.currentList)
            storeReducer({
                type: GlobalStoreActionType.UPDATE_CURRENT_LIST,
                payload: this.currentList
            });     
    }

    store.deleteSong = async function(){
        this.currentList.songs.splice(this.markDeleteSong,1);
        await api.updatePlaylistById(this.currentList._id,this.currentList)
        storeReducer({
            type: GlobalStoreActionType.UPDATE_CURRENT_LIST,
            payload: this.currentList
        });     
    }

    store.moveSong = async function(source, target){
        let temp  = this.currentList.songs[source]
        this.currentList.songs[source] = this.currentList.songs[target]
        this.currentList.songs[target] = temp
        this.setCurrentList();
    }





    // THESE ARE THE FUNCTIONS THAT WILL UPDATE OUR STORE AND
    // DRIVE THE STATE OF THE APPLICATION. WE'LL CALL THESE IN 
    // RESPONSE TO EVENTS INSIDE OUR COMPONENTS.

    // THIS FUNCTION PROCESSES CHANGING A LIST NAME
    store.changeListName = function (id, newName) {
        // GET THE LIST
        async function asyncChangeListName(id) {
            let response = await api.getPlaylistById(id);
            if (response.data.success) {
                let playlist = response.data.playlist;
                playlist.name = newName;
                async function updateList(playlist) {
                    response = await api.updatePlaylistById(playlist._id, playlist);
                    if (response.data.success) {
                        async function getListPairs(playlist) {
                            response = await api.getPlaylistPairs();
                            if (response.data.success) {
                                let pairsArray = response.data.idNamePairs;
                                storeReducer({
                                    type: GlobalStoreActionType.CHANGE_LIST_NAME,
                                    payload: {
                                        idNamePairs: pairsArray,
                                        playlist: playlist
                                    }
                                });
                            }
                        }
                        getListPairs(playlist);
                    }
                }
                updateList(playlist);
            }
        }
        asyncChangeListName(id);
    }

    // THIS FUNCTION PROCESSES CLOSING THE CURRENTLY LOADED LIST
    store.closeCurrentList = function () {
        storeReducer({
            type: GlobalStoreActionType.CLOSE_CURRENT_LIST,
            payload: {}
        });
    }

    // THIS FUNCTION LOADS ALL THE ID, NAME PAIRS SO WE CAN LIST ALL THE LISTS
    store.loadIdNamePairs = function () {
        async function asyncLoadIdNamePairs() {
            const response = await api.getPlaylistPairs();
            if (response.data.success) {
                let pairsArray = response.data.idNamePairs;
                storeReducer({
                    type: GlobalStoreActionType.LOAD_ID_NAME_PAIRS,
                    payload: pairsArray
                });
            }
            else {
                console.log("API FAILED TO GET THE LIST PAIRS");
            }
        }
        asyncLoadIdNamePairs();
    }

    store.setCurrentList = function (id) {
        async function asyncSetCurrentList(id) {
            let response = await api.getPlaylistById(id);
            if (response.data.success) {
                let playlist = response.data.playlist;

                if (response.data.success) {
                    storeReducer({
                        type: GlobalStoreActionType.SET_CURRENT_LIST,
                        payload: playlist
                    });
                    store.history.push("/playlist/" + playlist._id);
                }
            }
        }
        asyncSetCurrentList(id);
    }
    store.getPlaylistSize = function() {
        return store.currentList.songs.length;
    }
    store.undo = function () {
        tps.undoTransaction();
    }
    store.redo = function () {
        tps.doTransaction();
    }

    store.addMoveSongTransaction = function (oldIdx, newIdx){
        tps.addTransaction(new MoveSong_Transaction(this, oldIdx, newIdx));
    }

    // THIS FUNCTION ENABLES THE PROCESS OF EDITING A LIST NAME
    store.setIsListNameEditActive = function () {
        storeReducer({
            type: GlobalStoreActionType.SET_LIST_NAME_EDIT_ACTIVE,
            payload: null
        });
    }

    // THIS GIVES OUR STORE AND ITS REDUCER TO ANY COMPONENT THAT NEEDS IT
    return { store, storeReducer };
}
